//post fetch : post '/notes', to: 'notes#create'
//check that token match then post note attaches to user id
//
import React from 'react';
import {withRouter} from "react-router-dom";
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

class CreateNoteForm extends React.Component {

  createNewNote = e => {
    e.preventDefault();
    fetch("http://localhost:3000/api/v1/create", {
      method: "POST",
      body: JSON.stringify({
        formInfoHere: target.formInfoHere
      }),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(resp => resp.json())
      .then(this.props.createNoteApp)
  };

  render() {
    const style = { border: "1px solid brown", padding: "1rem", margin: "1rem" };
    return (
      <div style={style} className="CreateUserForm">
        <h2>Journal Entry</h2>
      <Form >
        <FormGroup>
          <Label for="exampleText">Title</Label>
          <Input type="text" name="text" id="exampleText" placeholder="Journal Title" />
        </FormGroup>
        <FormGroup>
          <Label for="exampleText">Location</Label>
          <Input type="text" name="text" id="exampleText" placeholder="City, State" />
        </FormGroup>
        <FormGroup>
          <Label for="exampleText">Text Area</Label>
          <Input type="textarea" name="text" id="exampleText" placeholder="Start Writing..."/>
        </FormGroup>
        <FormGroup>
          <Label for="exampleFile">Upload File</Label>
          <Input type="file" name="file" id="exampleFile" />
          <FormText color="muted">
            This is some placeholder block-level help text for the above input.
            It's a bit lighter and easily wraps to a new line.
          </FormText>
        </FormGroup>
        <FormGroup tag="fieldset">
          <legend>Radio Buttons</legend>
          <FormGroup check>
            <Label check>
              <Input type="radio" name="radio1" />{' '}
              Set Journal Entry to Private
            </Label>
          </FormGroup>
          {/* <FormGroup check>
            <Label check>
              <Input type="radio" name="radio1" />{' '}
              Journal Entry is Private
            </Label>
          </FormGroup>
          <FormGroup check disabled>
            <Label check>
              <Input type="radio" name="radio1" disabled />{' '}
              Option three is disabled
            </Label>
          </FormGroup> */}
        </FormGroup>
        <FormGroup check>
          <Label check>
            <Input type="checkbox" />{' '}
            Check me out
          </Label>
        </FormGroup>
        <Button>Submit</Button>
      </Form>
    </div>
    );
  }
}
export default withRouter(CreateNoteForm);
