import React from 'react';
import {withRouter} from "react-router-dom";
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

class CreateNoteForm extends React.Component {
  // constructor(props) {
  //   super(props);
  //   this.state =
    state = {
        title: "",
        content: "",
        location: "",
        // currentUser: this.props.currentUser
      };
  // }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  createNewNote = e => {
    e.preventDefault();
    const token = localStorage.token;
    if(token) {
       fetch("http://localhost:3000/api/v1/new", {
        method: "POST",
        body: JSON.stringify({
          title: target.formInfoHere,
          content: target.content,
          location: target.location
        }),
        headers: {
          "Content-Type": "application/json"
        }
      })
        .then(resp => resp.json())
        .then(this.props.createNoteApp)
    };
  };


  render() {
    const style = { border: "1px solid brown", padding: "1rem", margin: "1rem" };
    return (
      <div style={style} className="CreateUserForm">
        <h2>Journal Entry</h2>
      <Form onSubmit={this.createNewNote}>
        <FormGroup>
          <Label for="exampleText">Title</Label>
          <Input
            type="text"
            onChange={this.handleChange}
            name="text"
            id="exampleText"
            placeholder="Journal Title"
          />
        </FormGroup>
        <FormGroup>
          <Label for="exampleText">Location</Label>
          <Input
            type="text"
            onChange={this.handleChange}
            name="text"
            id="exampleText"
            placeholder="City, State"
          />
        </FormGroup>
        <FormGroup>
          <Label for="exampleText">Text Area</Label>
          <Input
            type="textarea"
            onChange={this.handleChange}
            name="text"
            id="exampleText"
            placeholder="Start Writing..."
          />
        </FormGroup>
        {/* <FormGroup>
