  //     }
  //   })
  //     .then(resp => resp.json())
  //     .then(data => {
  //       if (!data.error) {
  //         this.setState({
  //           currentUser: data,
  //           signedIn: true
  //         });
  //       }
  //     });
  // }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  logIn = e => {
    e.preventDefault();
    fetch("http://localhost:3000/api/v1/login", {
      method: "POST",
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password
      }),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(resp => resp.json())
      .then(this.props.logInApp)
  };

  getUserNotes() {
    if (this.state.signedIn === true) {
      console.log(this.state.currentUser.notes)
    }
    console.log("sign in")
  }

  render () {

    const style = { border: "1px solid green", padding: "1rem" };
    return (
      <div>
      <div style={style}>
        <form onSubmit={this.logIn}>
          <input
            type="text"
            onChange={this.handleChange}
            placeholder="username"
            name="username"
          />
          <input
            type="password"
            onChange={this.handleChange}
            placeholder="password"
            name="password"
          />
          <input type="submit" />
        </form>
      </div>



    </div>
    )
  }
}
