import {
  Container, Col, Form,
  FormGroup, Label, Input,
  Button,
} from 'reactstrap';

export default class UserSignIn extends Component {
  state = {
      username: "",
      password: "",
      currentUser: {},
      signedIn: false
    };

  // componentDidMount() {
